<script>
export default {
    name: 'HomePlaceholderElement',
    props: [
        'game',
        'videoSrc',
        'imgSrc'
    ],
    data() {
        return {
            homeHeight: 170,
            imagePath: this.imgSrc !== undefined ? require(`@/assets/img/games/backup/${this.imgSrc}`) : null,
            videoPath: this.videoSrc !== undefined ? require(`@/assets/img/games/backup/${this.videoSrc}`) : null,
        }
    },
    methods: {
        homeSetHeight() {
            this.homeHeight = Number(this.$refs.gameLink.$el.getBoundingClientRect().width * 0.574324).toFixed(4);
        }
    },
    computed: {
        homeGetLink() {
            let link = '/';
            switch (this.game) {
                case "Case Battles":
                    link = "battles"
                    break;
                case "Plinko":
                    link = "plinko"
                    break;
                case "Blackjack":
                    link = "blackjack"
                    break;
                case "Crash":
                    link = "crash"
                    break;
                case "Roulette":
                    link = "roulette"
                    break;
                case "Mines":
                    link = "mines"
                    break;
                case "Towers":
                    link = "towers"
                    break;
                case "Slide":
                    link = "slide"
                    break;
                case "Upgrader":
                    link = "upgrader"
                    break;
                default:
                    break;
            }
            return link;
        },
        homeGetBadgeText() {
            let link = this.game;
            let badge = "NEW";
            // if (link === 'battles') { badge = 'HOT'; }
            // else if (link === 'roulette') { badge = 'NEW'; }
            // else if (link === 'cases') { badge = 'NEW'; }

            return badge;
        }
    },
    mounted() {
        window.addEventListener('resize', this.homeSetHeight);
        this.homeSetHeight();
        if (this.videoSrc !== null && this.videoSrc !== undefined) {
            var video = document.getElementById(this.game);
            video.addEventListener('mouseover', function () {
                video.play();
            });
            video.addEventListener('mouseout', function () {
                video.pause();
                video.currentTime = 0;
            });
        }
    },
    destroyed() {
        window.removeEventListener('resize', this.homeSetHeight);
    }
}
</script>

